swordController.text.toString();    //api    var ipAddress = IpAddress(type: RequestType.json);    dynamic ip = await ipAddress.getIpAddress();    // print(ip.toString());    var endPoint = "$baseUrl/register";    var response = await http.post(        Uri.parse(endPoint),        body: {          "firstName": firstName,          "lastName": lastName,          "country": country,          // "identityType": identityType,          "cnic": cnic,          "phoneNo": phoneNo,          "email": email,          "password": password,          "confirmPassword": confirmPassword,        }    );    if (response.statusCode == 200) {      // Request was successful      print('Data sent to API successfully');    } else {      // Request failed      print('Failed to send data to API. Error: ${response.statusCode}');    }  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        iconTheme: const IconThemeData(color: Colors.black),        elevation: 0,        backgroundColor: Colors.blueGrey[50],      ),      body: Container(        padding: const EdgeInsets.all(10),        child: SingleChildScrollView(          child: Column(            mainAxisAlignment: MainAxisAlignment.center,            crossAxisAlignment: CrossAxisAlignment.stretch,            children: [              Image.asset(                "assets/HEC-Logo.png",                height: 100,              ),              const SizedBox(                height: 10,              ),              Container(                padding: const EdgeInsets.all(10),                decoration: BoxDecoration(                    borderRadius: BorderRadius.circular(10),                    color: Colors.white,                    boxShadow: const [                      BoxShadow(                          color: Colors.black12,                          blurRadius: 5,                          offset: Offset(0, 3))                    ]),    